        }
        int size = queue.size();
        for (int x = 0; x < size; x++) {
            int v = queue.pollLast();
            if (v < n - 1 && ratings[v] > ratings[v + 1] && record[v] <= record[v + 1]) {
                record[v] = record[v + 1] + 1;
            }
