class Solution {
    public String minWindow(String s, String t) {
        int[] bits = new int[52];
        int[] modi = new int[52];
        for (int x = 0; x < t.length(); x++) {
            addArr(bits, t.charAt(x));
            addArr(modi, t.charAt(x));
        }
        Deque<Integer> deque = new LinkedList<>();
        int si = -1;
        int ei = -1;
        for (int x=0; x<s.length(); x++) {
            char cc = s.charAt(x);
            if (getBit(bits, cc) > 0) {
                deque.offerLast(x);
                subArr(modi, cc);
                while (!deque.isEmpty()  && getBit(modi, s.charAt(deque.peekFirst())) < 0) {
                    addArr(modi, s.charAt(deque.peekFirst()));
                    deque.pollFirst();
                }
                // if (getBit(modi, cc) < 0 && !deque.isEmpty() && s.charAt(deque.peekFirst()) == cc) {
                //     addArr(modi, cc);
                //     deque.pollFirst();
                // }
                if (allZero(modi)) {
                    if (si == -1) {
                        si = deque.peekFirst();
                    }
                    if (ei == -1) {
                        ei = deque.peekLast();
                    }
                    if ((ei - si) > (deque.peekLast() - deque.peekFirst())) {
                        si = deque.peekFirst();
                        ei = deque.peekLast();
                    }
                }

            }

        }
        if (si == -1) {
            return "";
        } else {
            return s.substring(si, ei+1);
        }

    }

    boolean allZero (int[] arr) {
        for (int i : arr) {
            if (i > 0) {
                return false;
            }
        }
        return true;
    }

    int getBit (int[] arr, char c) {
        if (c < 'a' || c > 'z') {
            return arr[ (c - 'A' + 26)];
        } else {
            return arr[c - 'a'];
        }
    }

    void subArr (int[] arr, char c) {
        if (c < 'a' || c > 'z') {
            arr[ (c - 'A' + 26)]--;
        } else {
            arr[c - 'a']--;
        }
    }

    void addArr (int[] arr, char c) {
        if (c < 'a' || c > 'z') {
            arr[ (c - 'A' + 26)]++;
        } else {
            arr[c - 'a']++;
        }
    }
}

