    MyShader myshader(DEFAULT_VERTEX_FILE_PATH, DEFAULT_FRAGMENT_FILE_PATH);

    while (!glfwWindowShouldClose(window))
    {
        processInput(window);
        glClearColor(0.2f, 0.3f, 0.3f, 1.0f);
        glClear(GL_COLOR_BUFFER_BIT);
        myshader.use();

